import { useState } from "react"

export default function Cadastro() {
const[nome, setNome] = useState('')
const[email, setEmail] = useState('')
const[fone, setFone] = useState('')
    return (
        <div className="container">
            <h2 className="text-center">Novo contato</h2>
            <label className="form-label">Informe o nome</label>
            <input className="form-control" placeholder="Informe o nome" value={nome} onChange={txt=> setNome(txt.target.value)}/>

            <label className="form-label">Informe o email</label>
            <input className="form-control" type="text" placeholder="Informe o email" value={email} onChange={txt=> setNome(txt.target.value)}/>

            <label className="form-label">Informe o telefone</label>
            <input className="form-control" type="text" placeholder="Informe o telefone" value={fone} onChange={txt=> setEmail(txt.target.value)}/>

            <div className="mt-3 d-flex justify-content-between">
                <button className="btn btn-primary">Salvar</button>
                <button className="btn btn-danger">Cancelar</button>
            </div>

            {`${nome} ${email} ${fone}`}
        </div>
    )
}
